'use client';

import Image from 'next/image';
import { useState } from 'react';

interface BlogImageProps {
  src: string;
  alt: string;
  fill?: boolean;
  priority?: boolean;
  quality?: number;
  style?: React.CSSProperties;
  width?: number;
  height?: number;
  className?: string;
  sizes?: string;
}

export default function BlogImage({ 
  src, 
  alt, 
  fill, 
  priority = false, 
  quality = 75,
  style,
  width,
  height,
  className,
  sizes = '(max-width: 640px) 100vw, (max-width: 1024px) 50vw, 33vw'
}: BlogImageProps) {
  const [error, setError] = useState(false);
  const fallbackImage = 'data:image/svg+xml;base64,';

  const blurDataURL = 'data:image/jpeg;base64,';

  return (
    <Image
      src={error ? fallbackImage : src}
      alt={alt}
      fill={fill}
      width={width}
      height={height}
      priority={priority}
      quality={quality}
      sizes={sizes}
      className={className}
      style={style}
      placeholder="blur"
      blurDataURL={blurDataURL}
      onError={() => setError(true)}
      loading={priority ? 'eager' : 'lazy'}
    />
  );
}
